import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    age: '2',
    preferences: {
        goals: [],
        interests: []
    }
  },
  getters: {
  },
  mutations: {
    updateAge(state, props) {
      state.age = props 
      console.log('hello from state ! Age: ', state.age)
  }
  },
  actions: {
    updateAge: ({ commit }) => commit('updateAge')
  },
  modules: {
  }
})
