import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    firstname: '',
    age: '',
    preferences: {
      goals: [],
      interests: []
    }
  },
  getters: {
  },
  mutations: {
    updateAge (state, payload) {
      state.age = payload
    }
  },
  actions: {
    updateAge: ({ commit }) => commit('updateAge')
  },
  modules: {

  }
})
