import axios from 'axios';
import NavbarUser from '@/components/NavbarUser.vue';
export default {
  name: 'HomeView',

  data() {
    return {
      username: '',
      userInfos: {},
      bookList: [],
      newBooks: [],
      biographies: [],
      userSuggestion: []
    };
  },

  components: {
    NavbarUser
  },

  beforeMount() {
    axios.get('http://localhost:8001/user', {
      withCredentials: true
    }).then(res => {
      if (res.data.success) {
        // console.log(res.data.user)
        this.username = res.data.user.firstname;
        this.userInfos = res.data.user;
      }
    }).catch(err => {
      return console.log(err);
    });
  },

  mounted() {
    axios.get('http://localhost:8001/books', {
      withCredentials: true
    }).then(res => {
      if (res.data.success) {
        console.log(res.data);
        const bookList = res.data.bookList; // Getting a selection of 4 biographies:

        const biographies = bookList.filter(book => book.genre.toLowerCase() === 'biographie' || book.genre.toLowerCase() === 'autobiographie').length = 4;
        console.log('biographies', biographies);
        this.biographies = biographies; // Get a selection of four books :

        bookList.length = 4;
        console.log('bookList', bookList);
        this.bookList = bookList;
      }
    }).catch(err => {
      return console.log(err);
    });
  }

};
