import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    firstname: '',
    age: '',
    preferences: {
      goals: [],
      interests: []
    }
  },
  getters: {
  },
  mutations: {
    setAge (state, payload) {
      state.age = payload
    },
    setFirstName (state, payload) {
      state.firstname = payload
    },
    //Modify mutations to : delete or add Goal => same for interests
    setGoals (state, payload) {
      state.preferences.goals = payload
    },
    setInterests (state, payload) {
      state.preferences.interests = payload
    }
  },
  actions: {
    updateAge: ({ commit }) => commit('setAge'),
    updateFirstName: ({ commit }) => commit('setFirstName'),
    updateGoals: ({ commit }) => commit('setGoals'),
    updateInterests: ({ commit }) => commit('setInterests')
  },
  modules: {

  }
})
