import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state: {
    firstname: '',
    age: '',
    preferences: {
      goals: [],
      interests: []
    }
  },
  getters: {
  },
  mutations: {
    setAge (state, payload) {
      state.age = payload
    },
    setFirstName (state, payload) {
      state.firstname = payload
    },
    // Modify mutations to : delete or add Goal => same for interests
    addGoals (state, payload) {
      state.preferences.goals.push(payload)
    },
    deleteGoals (state, payload) {
      state.preferences.goals.forEach(goal => {
        if (goal === payload) {
          const index = state.preferences.goals.indexOf(goal)
          state.preferences.goals.splice(index, 1)
        }
      })
    },
    addInterests (state, payload) {
      state.preferences.interests.push(payload)
    },
    deleteInterests (state, payload) {
      state.preferences.interests.forEach(interest => {
        if (interest === payload) {
          const index = state.interests.interests.indexOf(interest)
          state.preferences.interests.splice(index, 1)
        }
      })
    }
  },
  actions: {
    updateAge: ({ commit }) => commit('setAge'),
    updateFirstName: ({ commit }) => commit('setFirstName'),
    addNewGoals: ({ commit }) => commit('addGoals'),
    removeGoals: ({ commit }) => commit('deleteGoals'),
    addNewInterests: ({ commit }) => commit('addInterests'),
    removeInterests: ({ commit }) => commit('deleteInterests')
  },
  modules: {

  }
})
